CREATE TABLE IF NOT EXISTS Errors(ErrorID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, TypeID INTEGER NOT NULL, Path VARCHAR, Line INT, MSG VARCHAR, First DATETIME, Last DATETIME, COUNT INT NOT NULL);
CREATE TABLE IF NOT EXISTS Type(TypeID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Language VARCHAR NOT NULL, TypeName VARCHAR NOT NULL, MSG VARCHAR, COUNT INT NOT NULL);
CREATE TABLE IF NOT EXISTS Language(Language VARCHAR NOT NULL, Regex VARCHAR NOT NULL, COUNT INT NOT NULL, PRIMARY KEY(Language)); 
CREATE TABLE IF NOT EXISTS Solution(SolutionID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, TypeID INTEGER NOT NULL, Priority INT NOT NULL, Solution VARCHAR NOT NULL, Solved INT NOT NULL, Unsolved INT NOT NULL);